UPDATE `quest_offer_reward` SET `RewardText` = 'Excellent travail, $n. Les tremblements se sont déjà un peu calmés.$b$bAvec de la chance, vos efforts nous laisseront assez de temps pour trouver un moyen de contrer les plans de Malygos.' WHERE `ID` = 11905;
UPDATE `quest_offer_reward` SET `RewardText` = 'Fascinant !$b$bEt ils sont agressifs, vous dites. Cela tendrait à suggérer qu''il y a une intention derrière cette manipulation.$B$BLe phénomène pourrait bien ne pas être naturel du tout…' WHERE `ID` = 11911;
UPDATE `quest_offer_reward` SET `RewardText` = 'C''est donc fait...$b$bNous n''oublierons jamais le sacrifice de Keristrasza pour cette cause.' WHERE `ID` = 11973;
UPDATE `quest_offer_reward` SET `RewardText` = 'Merci, $ghéros:héroïne; !$b$bGrâce à votre bravoure, Dalaran va pouvoir vivre un jour de plus.' WHERE `ID` = 29830;
UPDATE `quest_offer_reward` SET `RewardText` = 'Écoutez bien, $r.$b$bVotre travail ici ne fait que commencer !' WHERE `ID` = 13124;
